    public function getSourceContext(): Source
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Modifier l'utilisateur{% endblock %}

{% block body %}
<div class=\"container mt-5\">
    <div class=\"row justify-content-center\">
        <div class=\"col-lg-8\">
            <div class=\"card shadow-lg p-4\">
                <div class=\"card-header bg-primary text-white text-center\">
                    <h2>Modifier les informations de l'utilisateur</h2>
                </div>

                <div class=\"card-body\">
                    <!-- Formulaire pour modifier l'utilisateur -->
                    {{ form_start(form) }}
                    
                    <div class=\"form-group\">
                        {{ form_label(form.nom, 'Nom') }}
                        {{ form_widget(form.nom, { 'attr': { 'class': 'form-control', 'placeholder': 'Nom' } }) }}
                    </div>

                    <div class=\"form-group\">
                        {{ form_label(form.prenom, 'Prénom') }}
                        {{ form_widget(form.prenom, { 'attr': { 'class': 'form-control', 'placeholder': 'Prénom' } }) }}
                    </div>

                    <div class=\"form-group\">
                        {{ form_label(form.email, 'Email') }}
                        {{ form_widget(form.email, { 'attr': { 'class': 'form-control', 'placeholder': 'Email' } }) }}
                    </div>

                    <div class=\"form-group\">
                        {{ form_label(form.password, 'Mot de passe') }}
                        {{ form_widget(form.password, { 'attr': { 'class': 'form-control', 'placeholder': 'Mot de passe' } }) }}
                    </div>

                    <div class=\"form-group\">
                        {{ form_label(form.roles, 'Rôles') }}
                        {{ form_widget(form.roles, { 'attr': { 'class': 'form-check-input' } }) }}
                    </div>

                    <div class=\"d-flex justify-content-between\">
                        <a href=\"{{ path('app_profile') }}\" class=\"btn btn-secondary\">
                            <i class=\"fas fa-arrow-left\"></i> Retour au profil
                        </a>
                        <button type=\"submit\" class=\"btn btn-success\">
                            <i class=\"fas fa-save\"></i> Enregistrer les modifications
                        </button>
                    </div>

                    {{ form_end(form) }}
                </div>
            </div>
        </div>
    </div>
</div>
{% endblock %}", "user/edit.html.twig", "C:\\xampp\\htdocs\\Arcadia\\templates\\user\\edit.html.twig");
